mapper.insert:
INSERT INTO ITEM_CATEGORY (
    CATE_CODE
    , CATE_NAME
) VALUES (
    (SELECT 'CATE_'||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(CATE_CODE,6,3))),0) + 1,3,0) FROM ITEM_CATEGORY)
    , #{cateName}
)

INSERT INTO BOOK_ITEM (
    ITEM_CODE
    , ITEM_NAME
    , ITEM_PRICE
    , WRITER
    , PUBLISHER
	, TRANSLATOR
	, PUB_DATE
	, PAGE
	, ISBN
    , CATE_CODE
) VALUES (
    (SELECT 'ITEM_'||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(ITEM_CODE,6,3))),0) + 1,3,0) FROM BOOK_ITEM)
    , #{itemName}
    , #{itemPrice}
    , #{writer}
    , #{publisher}
    , #{translator}
    , #{pubDate}
    , #{page}
    , #{isbn}
    , #{cateCode}
)

INSERT INTO BOOK_IMG (
	IMG_CODE
	, ORIGIN_IMG_NAME
	, ATTACHED_IMG_NAME
	, ITEM_CODE
	, IS_MAIN
)

SELECT
	#{imgVO.imgCode}
	, #{imgVO.originImgName}
	, #{imgVO.attachedImgName}
	, #{imgVO.itemCode}
	, #{imgVO.isMain}
FROM DUAL
